/* Ecocycle - Eco-themed Styling with Green/Yellow/White Palette */

/* CSS Variables for Color Scheme */
:root {
    --primary-green: #2ecc71;
    --dark-green: #27ae60;
    --light-green: #a8e6cf;
    --accent-yellow: #f1c40f;
    --dark-yellow: #f39c12;
    --white: #ffffff;
    --off-white: #f8f9fa;
    --light-gray: #ecf0f1;
    --gray: #95a5a6;
    --dark-gray: #7f8c8d;
    --text-dark: #2c3e50;
    --shadow: rgba(0, 0, 0, 0.1);
    --shadow-hover: rgba(0, 0, 0, 0.15);
}

/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Poppins', sans-serif;
    background-color: var(--off-white);
    color: var(--text-dark);
    line-height: 1.6;
    overflow-x: hidden;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

/* Navigation Bar */
.navbar {
    background: linear-gradient(135deg, var(--primary-green) 0%, var(--dark-green) 100%);
    padding: 1rem 0;
    position: sticky;
    top: 0;
    z-index: 1000;
    box-shadow: 0 2px 10px var(--shadow);
}

.nav-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
}


.nav-links {
    display: flex;
    gap: 1.5rem;
    align-items: center;
}

.nav-link {
    color: var(--white);
    text-decoration: none;
    font-weight: 500;
    padding: 8px 16px;
    border-radius: 25px;
    transition: all 0.3s ease;
}

.nav-link:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: translateY(-2px);
}

.logout-btn {
    background: var(--accent-yellow);
    color: var(--text-dark);
}

.logout-btn:hover {
    background: var(--dark-yellow);
}

.download-btn {
    background: var(--dark-green);
}

.download-btn:hover {
    background: var(--primary-green);
}

.menu-toggle {
    display: none;
    flex-direction: column;
    gap: 5px;
    cursor: pointer;
}

.menu-toggle span {
    width: 25px;
    height: 3px;
    background: var(--white);
    border-radius: 3px;
    transition: all 0.3s ease;
}

/* Hero Section with Beautiful Background */
.hero-section {
    background: linear-gradient(135deg, var(--primary-green) 0%, var(--dark-green) 100%);
    background-image: 
        linear-gradient(135deg, rgba(46, 204, 113, 0.9) 0%, rgba(39, 174, 96, 0.9) 100%),
        url('data:image/svg+xml,<svg width="100" height="100" xmlns="http://www.w3.org/2000/svg"><defs><pattern id="grid" width="100" height="100" patternUnits="userSpaceOnUse"><path d="M 100 0 L 0 0 0 100" fill="none" stroke="rgba(255,255,255,0.1)" stroke-width="1"/></pattern></defs><rect width="100" height="100" fill="url(%23grid)"/></svg>');
    color: var(--white);
    padding: 80px 0;
    text-align: center;
    position: relative;
    overflow: hidden;
}

.hero-section::before {
    content: '';
    position: absolute;
    top: -50%;
    right: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(255,255,255,0.1) 1px, transparent 1px);
    background-size: 50px 50px;
    animation: drift 20s linear infinite;
}

@keyframes drift {
    0% { transform: translate(0, 0); }
    100% { transform: translate(50px, 50px); }
}

.hero-content {
    max-width: 800px;
    margin: 0 auto;
}

.hero-title {
    font-size: 3rem;
    font-weight: 700;
    margin-bottom: 1rem;
}

.hero-subtitle {
    font-size: 1.3rem;
    font-weight: 300;
}

/* Registration Section with Subtle Background */
.registration-section {
    padding: 60px 0;
    background: linear-gradient(to bottom, #f8f9fa 0%, #ffffff 100%);
    position: relative;
}

.registration-section::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: radial-gradient(circle at 20% 50%, rgba(46, 204, 113, 0.05) 0%, transparent 50%),
                      radial-gradient(circle at 80% 80%, rgba(241, 196, 15, 0.05) 0%, transparent 50%);
    pointer-events: none;
}

.registration-card {
    max-width: 500px;
    margin: 0 auto;
    background: var(--white);
    padding: 40px;
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
}

.registration-card h2 {
    color: var(--primary-green);
    margin-bottom: 10px;
    text-align: center;
}

.card-subtitle {
    text-align: center;
    color: var(--gray);
    margin-bottom: 30px;
}

/* Form Styles */
.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    font-weight: 500;
    color: var(--text-dark);
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 12px 15px;
    border: 2px solid var(--light-gray);
    border-radius: 8px;
    font-family: 'Poppins', sans-serif;
    font-size: 1rem;
    transition: all 0.3s ease;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--primary-green);
    box-shadow: 0 0 0 3px rgba(46, 204, 113, 0.1);
}

.form-group small {
    display: block;
    margin-top: 5px;
    color: var(--gray);
    font-size: 0.85rem;
}

/* Buttons */
.btn {
    padding: 12px 30px;
    border: none;
    border-radius: 25px;
    font-family: 'Poppins', sans-serif;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    text-decoration: none;
    display: inline-block;
}

.btn-primary {
    background: linear-gradient(135deg, var(--primary-green) 0%, var(--dark-green) 100%);
    color: var(--white);
    width: 100%;
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px var(--shadow-hover);
}

.btn-secondary {
    background: var(--gray);
    color: var(--white);
}

.btn-secondary:hover {
    background: var(--dark-gray);
}

.btn-add {
    background: var(--accent-yellow);
    color: var(--text-dark);
    padding: 8px 20px;
    font-size: 0.9rem;
}

.btn-add:hover {
    background: var(--dark-yellow);
    transform: translateY(-2px);
}

.btn-redeem {
    background: var(--primary-green);
    color: var(--white);
    padding: 8px 20px;
    font-size: 0.9rem;
}

.btn-redeem:hover {
    background: var(--dark-green);
}

.btn-disabled {
    background: var(--gray);
    cursor: not-allowed;
    opacity: 0.6;
}

.btn-disabled:hover {
    transform: none;
    background: var(--gray);
}

/* Page Header */
.page-header {
    background: linear-gradient(135deg, var(--primary-green) 0%, var(--dark-green) 100%);
    color: var(--white);
    padding: 50px 0;
    text-align: center;
}

.page-title {
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 10px;
}

.points-display {
    font-size: 1.2rem;
    margin-top: 15px;
}

.points-label {
    font-weight: 400;
}

.points-value {
    font-weight: 700;
    font-size: 1.5rem;
    color: var(--accent-yellow);
}

/* About Page */
.about-hero {
    background: linear-gradient(135deg, var(--primary-green) 0%, var(--dark-green) 100%);
    color: var(--white);
    padding: 60px 0;
    text-align: center;
}

.page-subtitle {
    font-size: 1.2rem;
    font-weight: 300;
    margin-top: 10px;
}

.about-section {
    padding: 60px 0;
}

.motto-section {
    text-align: center;
    padding: 40px 0;
    margin-bottom: 40px;
}

.motto-icon {
    font-size: 4rem;
    margin-bottom: 20px;
}

.motto-text {
    font-size: 2rem;
    color: var(--primary-green);
    font-weight: 600;
    font-style: italic;
}

.mission-section {
    background: var(--white);
    padding: 40px;
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
    margin-bottom: 40px;
}

.mission-section h2 {
    color: var(--primary-green);
    margin-bottom: 20px;
}

.mission-section p {
    line-height: 1.8;
    margin-bottom: 15px;
}

.how-it-works-section {
    margin-bottom: 60px;
}

.how-it-works-section h2 {
    text-align: center;
    color: var(--primary-green);
    margin-bottom: 40px;
    font-size: 2rem;
}

.steps-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 30px;
}

.step-card {
    background: var(--white);
    padding: 30px;
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
    text-align: center;
    transition: all 0.3s ease;
    position: relative;
}

.step-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 10px 30px var(--shadow-hover);
}

.step-number {
    position: absolute;
    top: -15px;
    right: 20px;
    background: var(--accent-yellow);
    width: 40px;
    height: 40px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 700;
    font-size: 1.2rem;
    color: var(--text-dark);
}

.step-icon {
    font-size: 3rem;
    margin-bottom: 15px;
}

.step-card h3 {
    color: var(--primary-green);
    margin-bottom: 10px;
}

/* Developers Section */
.developers-section {
    margin-top: 60px;
}

.developers-section h2 {
    text-align: center;
    color: var(--primary-green);
    margin-bottom: 10px;
    font-size: 2rem;
}

.section-subtitle {
    text-align: center;
    color: var(--gray);
    margin-bottom: 40px;
}

.developers-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 30px;
}

.developer-card {
    background: var(--white);
    padding: 30px;
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
    text-align: center;
    transition: all 0.3s ease;
}

.developer-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 10px 30px var(--shadow-hover);
}

.developer-image {
    width: 150px;
    height: 150px;
    margin: 0 auto 20px;
    border-radius: 50%;
    overflow: hidden;
    border: 5px solid var(--light-green);
}

.developer-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.developer-placeholder {
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, var(--primary-green) 0%, var(--dark-green) 100%);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 3rem;
    font-weight: 700;
    color: var(--white);
}

.developer-card h3 {
    color: var(--text-dark);
    margin-bottom: 8px;
}

.developer-role {
    color: var(--primary-green);
    font-weight: 600;
    margin-bottom: 15px;
}

.developer-bio {
    color: var(--gray);
    font-size: 0.9rem;
    line-height: 1.6;
}

.no-developers {
    text-align: center;
    padding: 40px;
}

.admin-note {
    margin-top: 10px;
    color: var(--gray);
}

.admin-note a {
    color: var(--primary-green);
    font-weight: 600;
}

/* Search Container */
.search-container {
    margin: 30px 0;
}

.search-input {
    width: 100%;
    padding: 15px 20px;
    border: 2px solid var(--light-gray);
    border-radius: 25px;
    font-family: 'Poppins', sans-serif;
    font-size: 1rem;
    transition: all 0.3s ease;
}

.search-input:focus {
    outline: none;
    border-color: var(--primary-green);
    box-shadow: 0 0 0 3px rgba(46, 204, 113, 0.1);
}

/* Waste Logs */
.waste-logs-section {
    padding: 60px 0;
}

.categories-section {
    margin: 40px 0;
}

.categories-section h2 {
    color: var(--primary-green);
    margin-bottom: 30px;
}

.categories-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 20px;
}

.category-card {
    background: var(--white);
    padding: 30px;
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
    text-align: center;
    transition: all 0.3s ease;
}

.category-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px var(--shadow-hover);
}

.category-icon {
    font-size: 3rem;
    margin-bottom: 15px;
}

.category-card h3 {
    color: var(--text-dark);
    margin-bottom: 20px;
    font-size: 1.1rem;
}

/* Logs Table */
.logs-section {
    margin-top: 40px;
}

.logs-section h2 {
    color: var(--primary-green);
    margin-bottom: 20px;
}

.logs-table-container {
    background: var(--white);
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
    overflow-x: auto;
}

.logs-table {
    width: 100%;
    border-collapse: collapse;
}

.logs-table thead {
    background: linear-gradient(135deg, var(--primary-green) 0%, var(--dark-green) 100%);
    color: var(--white);
}

.logs-table th,
.logs-table td {
    padding: 15px;
    text-align: left;
}

.logs-table tbody tr {
    border-bottom: 1px solid var(--light-gray);
    transition: background 0.3s ease;
}

.logs-table tbody tr:hover {
    background: var(--off-white);
}

.points-cell {
    color: var(--primary-green);
    font-weight: 600;
}

.no-data {
    text-align: center;
    padding: 40px;
    color: var(--gray);
}

/* Rewards Grid */
.rewards-section {
    padding: 60px 0;
}

.rewards-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 30px;
    margin-top: 30px;
}

.reward-card {
    background: var(--white);
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
    overflow: hidden;
    transition: all 0.3s ease;
}

.reward-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 10px 30px var(--shadow-hover);
}

.reward-image {
    width: 100%;
    height: 200px;
    overflow: hidden;
    background: var(--light-gray);
}

.reward-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.reward-placeholder {
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 4rem;
    background: linear-gradient(135deg, var(--light-green) 0%, var(--primary-green) 100%);
}

.reward-content {
    padding: 20px;
}

.reward-content h3 {
    color: var(--text-dark);
    margin-bottom: 10px;
}

.reward-description {
    color: var(--gray);
    font-size: 0.9rem;
    margin-bottom: 15px;
    min-height: 40px;
}

.reward-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 15px;
}

.reward-points {
    font-weight: 600;
    color: var(--text-dark);
}

.points-required {
    color: var(--primary-green);
    font-size: 1.3rem;
}

.no-rewards {
    text-align: center;
    padding: 60px 20px;
}

/* Dashboard */
.dashboard-section {
    padding: 60px 0;
}

.stats-card {
    background: var(--white);
    padding: 40px;
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
    margin-bottom: 40px;
}

.stats-card h2 {
    color: var(--primary-green);
    margin-bottom: 30px;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 30px;
}

.stat-item {
    display: flex;
    align-items: center;
    gap: 20px;
    padding: 20px;
    background: var(--off-white);
    border-radius: 10px;
    transition: all 0.3s ease;
}

.stat-item:hover {
    transform: translateX(10px);
    background: var(--light-green);
}

.stat-icon {
    font-size: 3rem;
}

.stat-content h3 {
    color: var(--text-dark);
    font-size: 1.8rem;
    margin-bottom: 5px;
}

.stat-content p {
    color: var(--gray);
}

/* Redeemed Section */
.redeemed-section {
    background: var(--white);
    padding: 40px;
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
    margin-bottom: 40px;
}

.redeemed-section h2 {
    color: var(--primary-green);
    margin-bottom: 30px;
}

.redeemed-list {
    display: grid;
    gap: 20px;
}

.redeemed-item {
    display: flex;
    align-items: center;
    gap: 20px;
    padding: 20px;
    background: var(--off-white);
    border-radius: 10px;
    border-left: 5px solid var(--primary-green);
}

.redeemed-icon {
    width: 50px;
    height: 50px;
    background: var(--primary-green);
    color: var(--white);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    font-weight: 700;
}

.redeemed-content h3 {
    color: var(--text-dark);
    margin-bottom: 5px;
}

.redeemed-points {
    color: var(--primary-green);
    font-weight: 600;
    font-size: 0.9rem;
}

.redeemed-date {
    color: var(--gray);
    font-size: 0.85rem;
}

/* Leaderboard */
.leaderboard-section {
    background: var(--white);
    padding: 40px;
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
}

.leaderboard-section h2 {
    color: var(--primary-green);
    margin-bottom: 10px;
    text-align: center;
}

.leaderboard-table {
    overflow-x: auto;
    margin-top: 20px;
}

.leaderboard-table table {
    width: 100%;
    border-collapse: collapse;
}

.leaderboard-table thead {
    background: linear-gradient(135deg, var(--primary-green) 0%, var(--dark-green) 100%);
    color: var(--white);
}

.leaderboard-table th,
.leaderboard-table td {
    padding: 15px;
    text-align: left;
}

.leaderboard-row {
    border-bottom: 1px solid var(--light-gray);
    transition: all 0.3s ease;
}

.leaderboard-row:hover {
    background: var(--off-white);
}

.rank-cell {
    font-size: 1.5rem;
    text-align: center;
}

.top-1 {
    background: linear-gradient(135deg, #ffd700 0%, #ffed4e 100%);
}

.top-2 {
    background: linear-gradient(135deg, #c0c0c0 0%, #e8e8e8 100%);
}

.top-3 {
    background: linear-gradient(135deg, #cd7f32 0%, #e6a75e 100%);
}

/* Modal */
.modal {
    display: none;
    position: fixed;
    z-index: 2000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    animation: fadeIn 0.3s ease;
}

.modal-content {
    background-color: var(--white);
    margin: 10% auto;
    padding: 40px;
    border-radius: 15px;
    max-width: 500px;
    box-shadow: 0 10px 40px var(--shadow);
    animation: slideDown 0.3s ease;
}

.close {
    color: var(--gray);
    float: right;
    font-size: 28px;
    font-weight: bold;
    cursor: pointer;
    transition: color 0.3s ease;
}

.close:hover {
    color: var(--text-dark);
}

.modal-content h2 {
    color: var(--primary-green);
    margin-bottom: 20px;
}

.modal-actions {
    display: flex;
    gap: 15px;
    margin-top: 30px;
}

.modal-actions .btn {
    flex: 1;
}

/* Admin Section */
.admin-section {
    padding: 60px 0;
}

.admin-card {
    max-width: 600px;
    margin: 0 auto;
    background: var(--white);
    padding: 40px;
    border-radius: 15px;
    box-shadow: 0 5px 20px var(--shadow);
}

/* Footer */
.footer {
    background: linear-gradient(135deg, var(--dark-green) 0%, var(--primary-green) 100%);
    color: var(--white);
    text-align: center;
    padding: 20px 0;
    margin-top: 60px;
}

/* Animations */
@keyframes fadeIn {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

@keyframes slideDown {
    from {
        transform: translateY(-50px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

@keyframes slideUp {
    from {
        transform: translateY(50px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

.fade-in {
    animation: fadeIn 0.8s ease;
}

.slide-up {
    animation: slideUp 0.8s ease;
}

/* Responsive Design */
@media (max-width: 968px) {
    .container {
        padding: 0 15px;
    }

    .logs-table-container,
    .leaderboard-table {
        overflow-x: auto;
        -webkit-overflow-scrolling: touch;
    }

    .logs-table,
    .leaderboard-table table {
        min-width: 600px;
    }

    .admin-nav-links {
        flex-wrap: wrap;
        gap: 10px;
    }

    .admin-nav-link {
        font-size: 0.9rem;
        padding: 6px 12px;
    }
}

@media (max-width: 768px) {
    .nav-links {
        position: fixed;
        left: -100%;
        top: 70px;
        flex-direction: column;
        background: var(--dark-green);
        width: 100%;
        text-align: center;
        transition: 0.3s;
        box-shadow: 0 10px 27px rgba(0, 0, 0, 0.05);
        padding: 20px 0;
        z-index: 999;
    }

    .nav-links.active {
        left: 0;
    }

    .menu-toggle {
        display: flex;
    }

